$(".send_message").click(function(){var n=$(".message").val().replace(/\n/g,"<br>");$.ajax({type:"GET",url:"/Api/SendMessage",async:!0,data:"msg="+n,success:function(){$(".send_message").removeClass("btn-info").addClass("btn-success")},error:function(){$(".send_message").removeClass("btn-info").addClass("btn-danger")}})});$(".get_messages").click(function(){$.ajax({type:"GET",url:"/api/api.php",async:!0,data:"method=showmsgs&count="+$(".messages_count").val()+"&toTable=1",success:function(n){$(".messages").html(n)}})});$(".show_tests").click(function(){$(".show_tests").text("Loading...");$(".show_tests").prop("disabled",!0);$.ajax({type:"GET",url:"../test/show.php",async:!0,success:function(n){$(".alltests").html(n);$(".show_tests").prop("disabled",!1);$(".show_tests").text("Запустить тесты")},error:function(){$(".show_tests").addClass("btn-danger");$(".show_tests").prop("disabled",!1);$(".show_tests").text("Запустить тесты")}})});